mapper.select:
select
  deposit_fixationId, deposit_fixationNumber, deposit_fixationCardID, deposit_fixationMoney, deposit_fixationLv, deposit_fixationBusinessType, deposit_fixationInTime, deposit_fixationOutTime, deposit_fixationUserId, deposit_state
from jsbank.deposit_fixation


        and deposit_fixationId = #{depositFixationid}


        and deposit_fixationNumber = #{depositFixationnumber}


        and deposit_fixationCardID = #{depositFixationcardid}


        and deposit_fixationMoney = #{depositFixationmoney}


        and deposit_fixationLv = #{depositFixationlv}


        and deposit_fixationBusinessType = #{depositFixationbusinesstype}


        and deposit_fixationInTime = #{depositFixationintime}


        and deposit_fixationOutTime = #{depositFixationouttime}


        and deposit_fixationUserId = #{depositFixationuserid}


        and deposit_state = #{depositState}

select deposit_fixationId, deposit_state,deposit_fixationNumber, deposit_fixationBusinessType,deposit_fixationCardID, deposit_fixationMoney, deposit_fixationLv, deposit_fixationInTime, deposit_fixationOutTime
from    deposit_fixation
where deposit_fixationUserId = #{userid}

mapper.update:
update deposit_fixation
set deposit_fixationMoney= deposit_fixationMoney - #{money}
where deposit_fixationId = #{infoid}

update deposit_fixation
set deposit_state = 1
where deposit_fixationId = #{informid}

update deposit_fixation
set deposit_state=1
where deposit_fixationId = #{businessId}
