mapper.select:
select
  branch_id, branch_name, branch_site, branch_phone, branch_data, branch_datatwo, branch_businessone, branch_businesstwo, branch_longitude, branch_dimensionality
from jsbank.branch


        and branch_id = #{branchId}


        and branch_name = #{branchName}


        and branch_site = #{branchSite}


        and branch_phone = #{branchPhone}


        and branch_data = #{branchData}


        and branch_datatwo = #{branchDatatwo}


        and branch_businessone = #{branchBusinessone}


        and branch_businesstwo = #{branchBusinesstwo}


        and branch_longitude = #{branchLongitude}


        and branch_dimensionality = #{branchDimensionality}
